let greet;
const DateTime = () => {
    const myDate = new Date();
    const hrs = myDate.getHours();

    if (hrs < 12) {
        greet = 'Good Morning';

    } else if (hrs >= 12 && hrs <= 17) {
        greet = 'Good Afternoon';

    }else if (hrs >= 17 && hrs <= 24) {
        greet = 'Good Evening';
    }
    return greet;

}

dateTime();

export default DateTime;
