import React , {useState} from 'react';
import './ExpenseForm.css';
const ExpenseForm = () => {
    //Multiple states
    // const [enteredTitle, setEnteredTitle] = useState('');
    // const [enteredAmount, setEnteredAmount] = useState('');
    // const [enteredDate, setEnteredDate] = useState('');

    const [userInput, setUserInput] = useState({
        setEnteredTitle: '',
        setEnteredAmount: '',
        setEnteredDate: '',
    });



    const titleChangeHandler = (event) => {

        //setEnteredTitle(event.target.value);

        // setUserInput({
        //     ...userInput,
        //     setEnteredTitle: event.target.value,
        // });

        setUserInput((prevState)=> {
            return {...prevState, setEnteredTitle: event.target.value};
        });

    };


    const AmountChangeHandler = (event) => {
        //setEnteredAmount(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     setEnteredAmount: event.target.value,
        // });

        setUserInput((prevState)=> {
            return {...prevState, setEnteredAmount: event.target.value};
        });



    };


    const DateChangeHandler = (event) => {
        //setEnteredDate(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     setEnteredDate: event.target.value,
        // });

        setUserInput((prevState)=> {
            return {...prevState, setEnteredDate: event.target.value};
        });

    };
    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: this.setEnteredTitle,
            amount: this.setEnteredAmount,
            date: new Date(this.setEnteredDate)
        };
    };


    return (

    <div>

        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>

                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={AmountChangeHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2022-12-31' onChange={DateChangeHandler}/>
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>

            </div>

        </form>

    </div>


    );
}

export default ExpenseForm;
